import React, { useState } from "react";
import { AppBar, Box, Button, IconButton, styled, Toolbar, Typography } from "@mui/material";
import { Poll, PostAdd, Search } from "@mui/icons-material";
import { Searchbox } from "./style/searchbar/Searchbox";
import { StyledInputBase } from "./style/searchbar/StyledInputBase";
import { SearchIconWrapper } from "./style/searchbar/SearchIconWrapper";
import { Iconbox } from "./style/iconbar/Iconbox";
import PostDialog from "../PostDialog/PostDialog";
import { Link } from "react-router-dom";

const ResponsiveSearch = styled(Search)(({ theme }) => ({
    [theme.breakpoints.down('md')]: {
        display: 'none'
    }
}));

const ResponsiveButton = styled(Button)(({ theme }) => ({
    [theme.breakpoints.down('sm')]: {
        display: 'none'
    }
}));

const ResponsiveIconButton = styled(IconButton)(({ theme }) => ({
    [theme.breakpoints.up('md')]: {
        display: 'none'
    }
}));

const NavBar = () => {
    const [open, setOpen] = useState(false);
    const [post, setPost] = useState("");

    const handleClickOpen = () => {
        setOpen(true);
    };

    const handleClose = (value) => {
        setOpen(false);
        // Decide what to do with value
        setPost(value);
    };

    return (
        <AppBar position="fixed">
            <Toolbar>
                <Typography variant="h6">
                    CSE Society
                </Typography>
                <Searchbox>
                    <SearchIconWrapper>
                        <ResponsiveSearch />
                    </SearchIconWrapper>

                    <StyledInputBase
                        placeholder="Searchâ€¦"
                        inputProps={
                            {
                                'aria-label': 'search'
                            }
                        }
                    />
                </Searchbox>
                <Box sx={{ flexGrow: 1 }} />
                <Iconbox>

                    <Link to={"/create-poll"}>
                        <ResponsiveIconButton>
                            <Poll />
                        </ResponsiveIconButton>
                    </Link>

                    <ResponsiveIconButton>
                        <PostAdd style />
                    </ResponsiveIconButton>


                    <Link to={"/create-poll"}>
                        <ResponsiveButton
                            variant="contained"
                            disableElevation
                            startIcon={<Poll />}
                        >
                            POLL MANAGEMENT
                        </ResponsiveButton>
                    </Link>

                    <ResponsiveButton
                        variant="contained"
                        disableElevation
                        startIcon={<PostAdd />}
                        onClick={handleClickOpen}
                    >
                        Create post
                    </ResponsiveButton>
                    <PostDialog open={open} onClose={handleClose} post={post} />
                </Iconbox>

            </Toolbar>
        </AppBar>
    );
};

export default NavBar;
